package com.example.miniprogect1.Service;import com.example.miniprogect1.domain.Ilchon;import com.example.miniprogect1.domain.Ilchonpatch;import com.example.miniprogect1.repository.IlchonRepository;import com.example.miniprogect1.repository.IlchonpatchRepository;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class IlchonService {    private final IlchonRepository ilchonRepository;    private final IlchonpatchRepository ilchonpatchRepository;    public IlchonService(IlchonRepository ilchonRepository, IlchonpatchRepository ilchonpatchRepository) {        this.ilchonRepository = ilchonRepository;        this.ilchonpatchRepository = ilchonpatchRepository;    }    public void addfirinds(Ilchon Ilch) {        ilchonRepository.save(Ilch);    }    public void addrequest(Ilchonpatch Ilch) {        ilchonpatchRepository.save(Ilch);    }    public void deletefriends(Ilchonpatch Ilch)    {        ilchonpatchRepository.delete(Ilch);    }    public List<Ilchonpatch> gust(String request) {        return ilchonpatchRepository.findByRequest(request);    }    public Ilchonpatch alarmfriend(String request)    {        return ilchonpatchRepository.findFirstByRequest(request);    }    public List<Ilchonpatch> trash(String request, String userName)    {        return ilchonpatchRepository.findByRequestAndNickName(request, userName);    }    public List<Ilchon> friendList(String Me)    {        return ilchonRepository.findByMe(Me);    }}