package com.example.miniprogect1.controller;import com.example.miniprogect1.Service.CashService;import com.example.miniprogect1.domain.User;import com.example.miniprogect1.domain.UserDotori;import jakarta.servlet.http.HttpSession;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/cash")public class CashController {    private CashService cashService;    @Autowired    public CashController(CashService cashService) {        this.cashService = cashService;    }    @PostMapping("/cash/point")    public @ResponseBody void CashCharge(@RequestParam Long amount, HttpSession session) {        User loginUser = (User)session.getAttribute("loginUser");        UserDotori user = new UserDotori();        user.setUserName(loginUser.getUserName());        user.setAmount(amount);        user.setDotori(amount/100);        cashService.ChargeCash(user);    }}