package com.example.miniprogect1.domain;import jakarta.persistence.*;import lombok.Data;import lombok.Getter;import lombok.Setter;import org.springframework.format.annotation.DateTimeFormat;import java.time.LocalDate;import java.time.LocalDateTime;@Entity@Getter@Setter@Table(name="user_dotori")public class UserDotori {    @Id    @GeneratedValue(            strategy = GenerationType.IDENTITY    )    private int id;    private Long amount;    private Long dotori;    private String userName;    @Column    @DateTimeFormat(pattern = "yyyy-MM-dd")    private LocalDate userRegDate = LocalDate.now();    @Column    @DateTimeFormat(pattern = "yyyy-MM-dd")    private LocalDateTime userRegDateTime = LocalDateTime.now();}