package com.example.miniprogect1.repository;import com.example.miniprogect1.domain.Ilchonpatch;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface IlchonpatchRepository extends JpaRepository<Ilchonpatch, Long> {    List<Ilchonpatch> findByRequest(String request);    List<Ilchonpatch> findByRequestAndNickName(String request, String nickName);    @Query(value = "select *" +            "   from ilchonpatch" +            "   where request = :request" +            "   limit 1", nativeQuery = true)    Ilchonpatch findFirstByRequest(@Param("request") String request);    List<Ilchonpatch> findAll();}